function getRandomInt(t,e){return Math.floor(Math.random()*(e-t))+t}function genRndQuote(){var t=getRandomInt(0,quotes.length);return quotes[t].text}var quotes=[{text:"Akira",author:"Laine"},{text:"Harry",author:"Potter"},{text:"Sherlock",author:"Holmes"},{text:"Kristian",author:"Vos"}];$(document).ready(function(){$("#genQuote").click(function(){$("#quote").html(genRndQuote())})});